    doc.text(44, 116, dummyKycData.address.district + ", " + dummyKycData.address.state + ", " + dummyKycData.address.pincode);


    doc.addImage(dummyKycData.image, 'JPEG', 150, 50, 40, 40);

    doc.addPage();
