    doc.text(20, 20, "Terms & Conditions");
    doc.line(10, 30, 180, 30);
    doc.setFontSize(16);
    doc.text(20, 40, doc.splitTextToSize(junkText + "\n\n" + junkText, 180));

    doc.addPage();
